import React from "react";

const API = async (url, abortSignal) => {
    const response = await fetch(url, { signal: abortSignal });
    return response.json();
  };

export const useStateAPI = (url) => {
    const [name, setName] = React.useState(null);
    const [profile, setProfile] = React.useState(null);
    React.useEffect(() => {
      // This is the abort controller that will cancel the fetch if the component was unmounted
      const controller = new AbortController();
      const signal = controller.signal;

      let mounted = true;

      const fetchData = async () => {
        try {
          const data = await API(url, signal);
          setName(data.graphql.user.username);
          setProfile(data.graphql.user.profile_pic_url);
        } catch (e) {
          setState(null);
        }
      };
      // it will fetch data only on mount so that
      if (mounted) {
        fetchData();
      }

      return () => {
        mounted = false;
        controller.abort();
      };
    }, [url]);


    const userData = {
        name,
        profile      
    }

    return userData;
  };
